const reducer = (state, action) => {
    switch (action.type) {
        case "SET_LOADING":
            return {
                ...state,
                loading: true,
            };
        case "GET_NEWS":
            return {
                ...state,
                loading: false,
                hits: action.payload.hits,
                nbPages: action.dispatch.nbPages,

            };
    }

    return state;
};

export default reducer;
