import React from 'react';
import {useGlobalContext} from './context'

const News = () => {
    const {loading, hits, hideNews} = useGlobalContext();
    if(loading) {
        return <h2>Loading..</h2>;
    }    

    return ( 
        <div className='news-div'>
            {hits.map((curNews) => {
                const { title, objectID, url, points, username, author} = curNews;
                let point="point";
                if(points>1)
                    point+='s';
                console.log(username);
                return (
                    <div className='news' key={objectID}>
                        <h2> {title} </h2>
                        <a href={url} target="_blank">
                            ({url})
                        </a>
                        <p> {points} {point} 
                        by {author} | <a href="#" onClick= {() => hideNews(objectID)} >hide </a> | past | discuss</p>
                      </div>
            );
        })}
    </div> );
};

export default News;
