import React from "react";
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { useSearchGlobalContext } from "./searchContext";
import SearchAppBar from "./searchAppBar";
import './search.css'

const SearchFilters = () => {

};

const Search = () => {
    const { hits, loading, query, searchText, content, selectContent, API, selectPopularity } = useSearchGlobalContext();
    if(loading) {
        return (
            <div >
        <form onSubmit={(evt) => evt.preventDefault()}>
            <div>
                <input type = "text" value = {query} placeholder="Search"
                onChange={(evt) => searchText(evt.target.value)}/>
            </div>
        </form>
        <h2>Loading..</h2>
        </div>
        );
    }    
    console.log("------------------");
    console.log(hits);
    console.log("at search")
    console.log("------------------");
    let len = hits.length
    console.log(hits.length);
    if(len==0)
        return (
            <div>
            <form onSubmit={(evt) => evt.preventDefault()}>
            <div>
                <input type = "text" value = {query} placeholder="Search"
                onChange={(evt) => searchText(evt.target.value)}/>
            </div>
        </form>
        {/* <Box sx={{ minWidth: 120 }}> */}
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Stories</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={content}
          label="Stories"
          onChange={(evt) => selectContent(evt.target.value)}
        >
        {/* undrerstand value and id and labelid  properties */}
          <MenuItem value={'stories&comments'}>All</MenuItem>
          <MenuItem value={'stories'}>Stories</MenuItem>
          <MenuItem value={'comments'}>Comments</MenuItem>
        </Select>
      </FormControl>
    {/* </Box> */}
        <h2>No Results</h2>
        </div>
        );
    return (
        <div>
            {/* <SearchAppBar/> */}
        <form onSubmit={(evt) => evt.preventDefault()}>
            <div>
                <input type = "text" value = {query} placeholder="Search"
                onChange={(evt) => searchText(evt.target.value)}/>
            </div>
        </form>
        {/* <Box sx={{ minWidth: 120 }}> */}
        <div className="filters">
      <FormControl className="drop-down">
        <InputLabel id="content">Content</InputLabel>
        <Select
        //   labelId="content"
          id="content"
          value={content}
          label={content}
          onChange={(evt) => selectContent(evt.target.value)}
        >
          <MenuItem value={"(story,comment)"}>All</MenuItem>
          <MenuItem value={"story"}>Stories</MenuItem>
          <MenuItem value={"comment"}>Comments</MenuItem>
        </Select>
      </FormControl>
    {/* </Box> */}
    <div className="text">
    <p> by </p>
    </div>
    <div className="drop-down">
    <FormControl>
        <InputLabel id="popularity">Popularity</InputLabel>
        <Select
        //   labelId="Popularity"
          id="popularity"
          value={API}
          label={API}
          onChange={(evt) => selectPopularity(evt.target.value)}
        >
          <MenuItem value={`http://hn.algolia.com/api/v1/search?`}>Popularity</MenuItem>
          <MenuItem value={`http://hn.algolia.com/api/v1/search_by_date?`}>Date</MenuItem>
        </Select>
      </FormControl>
      </div>
      <p className="text"> for </p>
    {/* <FormControl className="drop-down-menu-filter">
        <InputLabel id="content">Content</InputLabel>
        <Select
        //   labelId="content"
          id="content"
          value={content}
          label={content}
          onChange={(evt) => selectContent(evt.target.value)}
        >
          <MenuItem value={"(story,comment)"}>All</MenuItem>
          <MenuItem value={"story"}>Stories</MenuItem>
          <MenuItem value={"comment"}>Comments</MenuItem>
        </Select>
      </FormControl > */}
      </div>
        <div className='news-div'>
            {hits?.map((curPost) => {
                console.log("yes");
                const { title, objectID, url, points, author} = curPost;
                let point="point", val = points;
                if(points>1)
                    point+='s';
                else if(points==null)
                    val=0;
                if(title==null)
                    return <></>;
                return (
                    <div className='news' key={objectID}>
                        <span >
                            <h4 className='title'> {title} </h4>
                            <a href={url} target="_blank">
                            (Read More)
                            </a>
                        </span>
                        <p className='news-details'>{val} {point} by {author}  | past | discuss</p>
                      </div>
            );
        })}
        </div>
        {/* {
            curPosts.map(post=> (
                <li key={post.objectID} className='list-group-news'>
                    {post.story_title}
                </li>
            ))
        } */}
    {/* </div> ); */}
        </div>
    )
};
