import React, { useEffect } from 'react';
// 28:28
const News = () => {
    let loading = true;
    let API = "https://hn.algolia.com/api";


    const fetchAPI = async (url) => {
        try {
            const res = await fetch(url);
            const news = await res.json();
            loading = false;
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
      fetchAPI(API);

    }, []);

    if(loading) {
        return <h2>Loading..</h2>
    }

    return ( <div>News</div> );
};

export default News;
