import React from 'react';
import {useGlobalContext} from './context'

const News = () => {
    const {loading, hits} = useGlobalContext();
    if(loading) {
        return <h2>Loading..</h2>;
    }    

    return ( <div className='news-div'>
        {hits.map((curNews) => {
            const {title, objectID, points, url} = curNews;
            return (
                <div key={objectID}>
                    <><h2> {title}</h2></>
                    <p> {points} point | hide | <a href={url} target="_blank">Read more </a></p>
                </div>
            );
        })}
    </div> );
};

export default News;
