import React, { useContext, useEffect, useReducer } from "react";
import searchReducer from './searchReducer.jsx';

let API = "http://hn.algolia.com/api/v1/search?";

const initialSearchState = {
    loading: true,
    query: "",
    nbPages: 0,
    page: 0,
    hits: [],
};

const AppSearchContext = React.createContext();

const AppSearchProvider = ({children}) => {

    const [state, dispatch] = useReducer(searchReducer, initialSearchState);

    const fetchSearchAPIData = async(url) => {
        dispatch({type: "SET_LOADING"});

        try {
            const res = await fetch(url);
            const data = await res.json();
            console.log("-----------------");
            console.log("in searchContext");
            console.log(data);
            console.log("-----------------");

            dispatch({
                type: "GET_NEWS",
                payload: {
                    hits: data.hits,
                    nbPages: data.nbPages,
                },
            });

        } catch(error) {
            console.log(error);
        }
    };

    const searchText = (searchQuery) => {
        console.log("-------------------");
        console.log("in searchText");
        console.log(searchQuery);
        console.log("-------------------");

        dispatch({ type: "SEARCH_TEXT", payload: searchQuery });

    };

    // const getNextPage = () => {
    //     dispatch({type: "NEXT_PAGE",});
    // };

    // const getPrevPage = () => {
    //     dispatch({type: "PREV_PAGE",});
    // };

    useEffect(() => {
        if(state.query==undefined)
            s
        console.log(`${API}query=${state.query}&page=${state.page}`);
        fetchSearchAPIData(`${API}query=${state.query}&page=${state.page}`);
    }, [state.page, state.query]);

    return (
        <AppSearchContext.Provider value={{...state, searchText}}>
            {children}
        </AppSearchContext.Provider>
    );
};

const useSearchGlobalContext = () => {
    return useContext(AppSearchContext);
};

export { useSearchGlobalContext, AppSearchProvider };
