import React from 'react';
import {useGlobalContext} from './context'

const News = () => {
    const {loading, hits} = useGlobalContext();
    if(loading) {
        return <h2>Loading..</h2>;
    }    

    return ( <div className='news-div'>
        {hits.map((curNews) => {
            const {title, objectID, points, url} = curNews;
            return (

            <h2>{curNews.title}</h2>);
        })}
    </div> );
};

export default News;
