import React from "react";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { useSearchGlobalContext } from "./searchContext";
import SearchAppBar from "./searchAppBar";
import "./search.css";

const SearchFilters = () => {
  const {
    hits,
    // loading,
    query,
    searchText,
    content,
    selectContent,
    API,
    selectPopularity,
    duration,

  } = useSearchGlobalContext();

  const [showComment, setShowComment] = React.useState(content.type!=='story'?true:false);
  const commentArray = hits.map((data) => {
    let temp;
    if(hits.tags.includes("comment")) {
      temp = data;
    }
    return data;
  })
  console.log(commentArray);
  console.log(content);
  return (
    <div>
      {/* <SearchAppBar/> */}
      <form onSubmit={(evt) => evt.preventDefault()}>
        <div>
          <input
            type="text"
            value={query}
            placeholder="Search"
            onChange={(evt) => searchText(evt.target.value)}
          />
        </div>
      </form>
      <div className="filters">
      <span
          className="text"> Search
        </span>
        <Box sx={{ display: "inline" }}>
          <FormControl>
            <InputLabel id="content">Content</InputLabel>
            <Select
              //   labelId="content"
              id="content"
              value={content}
              label={content}
              onChange={(evt) => selectContent(evt.target.value)}
            >
              <MenuItem value={"(story,comment)"}>All</MenuItem>
              <MenuItem value={"story"}>Stories</MenuItem>
              <MenuItem value={"comment"}>Comments</MenuItem>
            </Select>
          </FormControl>
        </Box>
        <span className="text"> by  </span>
        <Box sx={{ display: "inline" }}>
          <FormControl>
            <InputLabel id="popularity">Popularity</InputLabel>
            <Select
              //   labelId="Popularity"
              id="popularity"
              value={API}
              label={API}
              onChange={(evt) => selectPopularity(evt.target.value)}
            >
              <MenuItem value={`http://hn.algolia.com/api/v1/search?`}>
                Popularity
              </MenuItem>
              <MenuItem value={`http://hn.algolia.com/api/v1/search_by_date?`}>
                Date
              </MenuItem>
            </Select>
          </FormControl>
        </Box>
        <span
          className="text"> for
        </span>
        <Box sx={{ display: "inline" }}>

        <FormControl>
          <InputLabel id="duration">Duration</InputLabel>
          <Select
            //   labelId="content"
            id="duration"
            value={duration}
            label={duration}
            onChange={(evt) => selectContent(evt.target.value)}
          >
            <MenuItem value={-1}>All time</MenuItem>
            <MenuItem value={Date.now() / 1000 - 24 * 60 * 60}>
              Last 24h
            </MenuItem>
            <MenuItem value={Date.now() / 1000 - 7 * 24 * 60 * 60}>
              Past Week
            </MenuItem>
            <MenuItem value={Date.now() / 1000 - 30 * 24 * 60 * 60}>
              Past Month
            </MenuItem>
            <MenuItem value={Date.now() / 1000 - 365 * 24 * 60 * 60}>
              Past Year
            </MenuItem>
            <MenuItem value={"comment"}>Custom Range</MenuItem>
          </Select>
        </FormControl>
        </Box>

      </div>
      <div className="news-div">
        {hits?.map((curPost) => {
          // console.log("yes");
          const { title, objectID, url, points, author } = curPost;
          let point = "point",
            val = points;
          if (points > 1) point += "s";
          else if (points == null) val = 0;
          if (title == null) return <></>;
          return (

            <div className="news" key={objectID}>
              {console.log(showComment)}
              {showComment && <div>comment </div>}
              <span>
                <h4 className="title"> {title} </h4>
                <a href={url} target="_blank">
                  (Read More)
                </a>
              </span>
              <p className="news-details">
                {val} {point} by {author} | past | discuss
              </p>
            </div>
          );
        })}
      </div>
      {/* {
            curPosts.map(post=> (
                <li key={post.objectID} className='list-group-news'>
                    {post.story_title}
                </li>
            ))
        } */}
      {/* </div> ); */}
    </div>
  );
};

const Search = () => {
  const {
    hits,
    loading,
    query,
    searchText,
    content,
    selectContent,
  } = useSearchGlobalContext();
  if (loading) {
    return (
      <div>
        <form onSubmit={(evt) => evt.preventDefault()}>
          <div>
            <input
              type="text"
              value={query}
              placeholder="Search"
              onChange={(evt) => searchText(evt.target.value)}
            />
          </div>
        </form>
        <h2>Loading..</h2>
      </div>
    );
  }
  console.log("------------------");
  console.log(hits);
  console.log("at search");
  console.log("------------------");
  let len = hits.length;
  console.log(hits.length);
  if (len == 0)
    return (
      <div>
        <form onSubmit={(evt) => evt.preventDefault()}>
          <div>
            <input
              type="text"
              value={query}
              placeholder="Search"
              onChange={(evt) => searchText(evt.target.value)}
            />
          </div>
        </form>
        {/* <Box sx={{ minWidth: 120 }}> */}
        <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">Stories</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={content}
            label="Stories"
            onChange={(evt) => selectContent(evt.target.value)}
          >
            {/* undrerstand value and id and labelid  properties */}
            <MenuItem value={"stories&comments"}>All</MenuItem>
            <MenuItem value={"stories"}>Stories</MenuItem>
            <MenuItem value={"comments"}>Comments</MenuItem>
          </Select>
        </FormControl>
        {/* </Box> */}
        <h2>No Results</h2>
      </div>
    );
  return <SearchFilters />;
};
//SEARCH API SE QUERY SEARCH RESULTS ARE FINE BUT WITH SEARCHBYDATE API QUERY RESULTS ARE MESSED UP
export default Search;
